import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
    const [leaves, setLeaves] = useState([]);
    const token = localStorage.getItem('token');

    useEffect(() => {
        const fetchLeaves = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/leaves/admin`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setLeaves(response.data);
            } catch (error) {
                console.error('Error fetching leaves:', error);
            }
        };

        fetchLeaves();
    }, [token]);

    const handleUpdateLeaveStatus = async (leaveId, status) => {
        try {
            await axios.post(`${process.env.REACT_APP_API_URL}/leaves/status`, { id: leaveId, status }, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setLeaves(leaves.map(leave => (leave.id === leaveId ? { ...leave, status } : leave)));
        } catch (error) {
            console.error('Error updating leave status:', error);
        }
    };

    return (
        <div className="admin-dashboard">
            <h2>Welcome, Admin!</h2>
            <h3>Leave Requests</h3>
            <ul>
                {leaves.map((leave) => (
                    <li key={leave.id}>
                        <div>
                            <strong>Student:</strong> {leave.student.username} <br />
                            <strong>Leave Type:</strong> {leave.type} <br />
                            <strong>From:</strong> {leave.fromDate} <br />
                            <strong>To:</strong> {leave.toDate} <br />
                            <strong>Status:</strong> {leave.status} <br />
                            <strong>Reason:</strong> {leave.reason} <br />
                            <button onClick={() => handleUpdateLeaveStatus(leave.id, 'approved')}>Approve</button>
                            <button onClick={() => handleUpdateLeaveStatus(leave.id, 'denied')}>Deny</button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default AdminDashboard;
