import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation

const StudentDashboard = () => {
    const [leaves, setLeaves] = useState([]);
    const [studentDetails, setStudentDetails] = useState(null); // State for student details
    const [loading, setLoading] = useState(true); // State to manage loading
    const [error, setError] = useState(null); // State to manage errors
    const token = localStorage.getItem('token');
    const navigate = useNavigate(); // Initialize useNavigate

    useEffect(() => {
        const fetchLeavesAndStudentDetails = async () => {
            setLoading(true); // Set loading to true
            try {
                // Fetch leaves
                const leavesResponse = await axios.get(`${process.env.REACT_APP_API_URL}/leaves/student`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setLeaves(leavesResponse.data);

                // Fetch student details (assuming you have an endpoint for this)
                const studentResponse = await axios.get(`${process.env.REACT_APP_API_URL}/students/me`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setStudentDetails(studentResponse.data);
            } catch (err) {
                setError('Failed to fetch data. Please try again later.');
                console.error('Error fetching data:', err);
            } finally {
                setLoading(false); // Set loading to false
            }
        };

        fetchLeavesAndStudentDetails();
    }, [token]);

    const handleApplyLeave = () => {
        navigate('/apply-leave'); // Navigate to the ApplyLeave component
    };

    const handleDeleteLeave = async (leaveId) => {
        // Handle leave deletion
        try {
            await axios.delete(`${process.env.REACT_APP_API_URL}/leaves/${leaveId}`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            // Refresh leaves after deletion
            setLeaves(leaves.filter(leave => leave.id !== leaveId));
        } catch (error) {
            console.error('Error deleting leave:', error);
            setError('Failed to delete leave. Please try again.');
        }
    };

    return (
        <div className="student-dashboard">
            <h2>Welcome, {studentDetails.username}!</h2>
            {loading && <p>Loading your details...</p>} {/* Show loading message */}
            {error && <p className="error">{error}</p>} {/* Show error message */}

            {studentDetails && ( // Check if studentDetails is available
                <div className="student-details">
                    <h3>Your Details</h3>
                    <p>ID: {studentDetails.id}</p>
                    <p>Username: {studentDetails.username}</p>
                </div>
            )}

            <button onClick={handleApplyLeave}>Apply Leave</button>
            <h3>Your Leaves</h3>
            {loading && <p>Loading your leaves...</p>} {/* Show loading message */}
            {error && <p className="error">{error}</p>} {/* Show error message */}
            <ul>
                {leaves.map((leave) => (
                    <li key={leave.id}>
                        {leave.type} from {leave.fromDate} to {leave.toDate} - Status: {leave.status}
                        <button onClick={() => handleDeleteLeave(leave.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default StudentDashboard;
