import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Updated to useNavigate

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState('student');
    const navigate = useNavigate(); // Updated from useHistory to useNavigate

    const handleLogin = async (e) => {
        e.preventDefault();
        const apiUrl = role === 'student' ? '/students/login' : '/admins/login';
        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}${apiUrl}`, { username, password });
            localStorage.setItem('token', response.data.token);
            // Redirect based on role
            if (role === 'student') {
                navigate('/student-dashboard'); // Updated route to match your structure
            } else {
                navigate('/admin-dashboard'); // Updated route to match your structure
            }
        } catch (error) {
            const errorMessage = error.response?.data?.message || 'Unexpected error occurred';
           // alert('Login failed: ' + errorMessage);

            // Optionally, show an error message to the user
            const errorContainer = document.getElementById('error-container');
            if (errorContainer) {
                errorContainer.textContent = errorMessage;
                errorContainer.style.color = 'red';
            }
        }

    };

    return (
        <div className="login-container">
            <h2>Login</h2>
            <form onSubmit={handleLogin}>
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <select onChange={(e) => setRole(e.target.value)} value={role}>
                    <option value="student">Student</option>
                    <option value="admin">Admin</option>
                </select>
                <button type="submit">Login</button>
            </form>
        </div>
    );
};

export default Login;
