import React, { useState } from 'react';
import axios from 'axios';
import '.src/styles/styles.css'; // Adjust the path as needed based on your project structure

const ForgotPassword = () => {
    const [username, setUsername] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [message, setMessage] = useState('');
    const [error, setError] = useState('');

    const handlePasswordReset = async () => {
        if (newPassword !== confirmPassword) {
            setError('Passwords do not match');
            setMessage('');
            return;
        }

        try {
            await axios.post(`${process.env.REACT_APP_API_URL}/auth/reset-password`, {
                username,
                password: newPassword,
            });
            setMessage('Password reset successfully. You can now log in.');
            setError('');
        } catch (err) {
            console.error('Error resetting password:', err);
            if (err.response && err.response.status === 404) {
                setError('Username not found. Please check and try again.');
            } else {
                setError('Failed to reset password. Please try again.');
            }
            setMessage('');
        }
    };

    return (
        <div className="forgot-password-container">
            <h2>Reset Password</h2>
            <div className="form-group">
                <input
                    type="text"
                    placeholder="Enter your username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    className="form-input"
                />
            </div>
            <div className="form-group">
                <input
                    type="password"
                    placeholder="Enter new password"
                    value={newPassword}
                    onChange={(e) => setNewPassword(e.target.value)}
                    className="form-input"
                />
            </div>
            <div className="form-group">
                <input
                    type="password"
                    placeholder="Confirm new password"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    className="form-input"
                />
            </div>
            <button onClick={handlePasswordReset} className="form-button">Reset Password</button>
            {message && <p className="success">{message}</p>}
            {error && <p className="error" style={{ color: 'red' }}>{error}</p>}
        </div>
    );
};

export default ForgotPassword;
