import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const ForgotPassword = () => {
    const [username, setUsername] = useState('');
    const [message, setMessage] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handlePasswordReset = async () => {
        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/auth/forgot-password`, { username });
            if (response.data.exists) {
                // Redirect to ResetPassword component if the user exists
                navigate(`/reset-password/${username}`); // Assuming you want to pass the username as a parameter
            } else {
                setError('Username not found.');
            }
        } catch (err) {
            console.error('Error sending password reset:', err);
            setError('Failed to send reset request. Please try again.');
        }
    };

    return (
        <div className="forgot-password-container">
            <h2>Forgot Password</h2>
            <input
                type="text"
                placeholder="Enter your username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
            />
            <button onClick={handlePasswordReset}>Send Reset Request</button>
            {message && <p className="success">{message}</p>}
            {error && <p className="error" style={{ color: 'red' }}>{error}</p>}
        </div>
    );
};

export default ForgotPassword;
