import React, { useEffect, useState } from 'react';
import axios from 'axios';

const StudentDashboard = () => {
  const [leaves, setLeaves] = useState([]);
  const [leaveData, setLeaveData] = useState({
    type: '',
    fromDate: '',
    toDate: '',
    reason: '',
  });
  const [loading, setLoading] = useState(false); // Loading state
  const [error, setError] = useState(''); // Error state

  const studentId = localStorage.getItem('studentId'); // Assume studentId is stored during login

  useEffect(() => {
    const fetchLeaves = async () => {
      setLoading(true);
      setError(''); // Reset error
      try {
        const response = await axios.get(`/api/students/leaves/${studentId}`);
        setLeaves(response.data);
      } catch (error) {
        console.error('Error fetching leaves:', error);
        setError('Failed to fetch leaves. Please try again later.'); // Set error message
      } finally {
        setLoading(false);
      }
    };
    fetchLeaves();
  }, [studentId]);

  const handleApplyLeave = async (e) => {
    e.preventDefault();

    // Log studentId to check its value
    console.log('Student ID:', studentId);

    // Check if studentId is not null or undefined
    if (!studentId) {
        alert('Student ID is missing. Please log in again.');
        return;
    }

    try {
        await axios.post('http://localhost:5000/api/students/apply', { ...leaveData, studentId });
        alert('Leave applied successfully!');

        // Refresh leaves after applying
        const response = await axios.get(`/api/students/leaves/${studentId}`);
        setLeaves(response.data);
    } catch (error) {
        console.error('Error applying leave:', error);
        alert('Failed to apply leave. Please try again.');
    }
};


  const handleCancelLeave = async (leaveId) => {
    if (window.confirm('Are you sure you want to cancel this leave?')) {
      try {
        await axios.delete(`/api/students/leaves/cancel/${leaveId}`);
        alert('Leave canceled successfully!');
        // Refresh leaves after cancellation
        const response = await axios.get(`/api/students/leaves/${studentId}`);
        setLeaves(response.data);
      } catch (error) {
        console.error('Error canceling leave:', error);
        alert('Failed to cancel leave. Please try again.'); // Consider showing a detailed error
      }
    }
  };

  return (
    <div>
      <h2>Student Dashboard</h2>
      <form onSubmit={handleApplyLeave}>
        <input
          type="text"
          placeholder="Type of Leave"
          value={leaveData.type}
          onChange={(e) => setLeaveData({ ...leaveData, type: e.target.value })}
          required
        />
        <input
          type="date"
          value={leaveData.fromDate}
          onChange={(e) => setLeaveData({ ...leaveData, fromDate: e.target.value })}
          required
        />
        <input
          type="date"
          value={leaveData.toDate}
          onChange={(e) => setLeaveData({ ...leaveData, toDate: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Reason"
          value={leaveData.reason}
          onChange={(e) => setLeaveData({ ...leaveData, reason: e.target.value })}
          required
        />
        <button type="submit">Apply Leave</button>
      </form>

      {loading ? (
        <p>Loading your leaves...</p> // Show loading message
      ) : error ? (
        <p style={{ color: 'red' }}>{error}</p> // Show error message
      ) : (
        <>
          <h3>Your Leave Applications</h3>
          <ul>
            {leaves.map((leave) => (
              <li key={leave.id}>
                {leave.type} from {leave.fromDate} to {leave.toDate} - Status: {leave.status}
                <button onClick={() => handleCancelLeave(leave.id)}>Cancel Leave</button>
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
};

export default StudentDashboard;
