import React, { useState } from 'react';
import axios from 'axios';
import Navbar from '../components/Navbar';

function StudentDashboard() {
    const [leave, setLeave] = useState({
        typeOfLeave: '',
        fromDate: '',
        toDate: '',
        reason: '',
    });

    const applyLeave = async () => {
        try {
            const token = localStorage.getItem('token');
            await axios.post('http://localhost:5000/api/leaves/apply', leave, {
                headers: { Authorization: `Bearer ${token}` },
            });
            alert('Leave applied successfully');
        } catch (error) {
            console.error('Error applying leave:', error.response.data.message);
        }
    };

    return (
        <div>
            <Navbar />
            <h1>Student Dashboard</h1>
            <form>
                <input
                    type="text"
                    placeholder="Type of Leave"
                    value={leave.typeOfLeave}
                    onChange={(e) => setLeave({ ...leave, typeOfLeave: e.target.value })}
                />
                <input
                    type="date"
                    value={leave.fromDate}
                    onChange={(e) => setLeave({ ...leave, fromDate: e.target.value })}
                />
                <input
                    type="date"
                    value={leave.toDate}
                    onChange={(e) => setLeave({ ...leave, toDate: e.target.value })}
                />
                <textarea
                    placeholder="Reason"
                    value={leave.reason}
                    onChange={(e) => setLeave({ ...leave, reason: e.target.value })}
                />
                <button type="button" onClick={applyLeave}>Apply Leave</button>
            </form>
        </div>
    );
}

export default StudentDashboard;
