import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const StudentDashboard = () => {
    const [leaves, setLeaves] = useState([]);
    const [studentDetails, setStudentDetails] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const token = localStorage.getItem('token');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchLeavesAndStudentDetails = async () => {
            setLoading(true);
            try {
                const leavesResponse = await axios.get(`${process.env.REACT_APP_API_URL}/leaves/student`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setLeaves(leavesResponse.data);

                const studentResponse = await axios.get(`${process.env.REACT_APP_API_URL}/students/me`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setStudentDetails(studentResponse.data);
            } catch (err) {
                setError('Failed to fetch data. Please try again later.');
                console.error('Error fetching data:', err);
            } finally {
                setLoading(false);
            }
        };

        fetchLeavesAndStudentDetails();
    }, [token]);

    const handleApplyLeave = () => {
        navigate('/apply-leave');
    };

    const handleCancelLeave = async (leaveId) => {
        try {
            await axios.post(`${process.env.REACT_APP_API_URL}/leaves/cancel`, { leaveId }, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setLeaves(leaves.map(leave => (leave.id === leaveId ? { ...leave, status: 'cancellation_pending' } : leave)));
        } catch (error) {
            console.error('Error requesting cancellation:', error);
        }
    };

    const handleDeleteLeave = async (leaveId) => {
        try {
            await axios.delete(`${process.env.REACT_APP_API_URL}/leaves/${leaveId}`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setLeaves(leaves.filter(leave => leave.id !== leaveId));
        } catch (error) {
            console.error('Error deleting leave:', error);
            setError('Failed to delete leave. Please try again.');
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/');
    };

    return (
        <div className="student-dashboard">
            {loading ? (
                <p>Loading your details...</p>
            ) : error ? (
                <p className="error">{error}</p>
            ) : (
                <>
                    {studentDetails ? (
                        <>
                            <h2>Welcome, {studentDetails.username}!</h2>
                            <div className="student-details">
                                <h3>Your Details</h3>
                                <p>Id: {studentDetails.id}</p>
                                <p>Username: {studentDetails.username}</p>
                            </div>
                        </>
                    ) : (
                        <p>No student details available.</p>
                    )}

                    <button onClick={handleApplyLeave}>Apply for Leave</button>

                    <h3>Your Leave Requests</h3>
                    <ul>
                        {leaves.map((leave) => (
                            <li key={leave.id}>
                                <p><strong>Leave Type:</strong> {leave.type}</p>
                                <p><strong>From:</strong> {new Date(leave.fromDate).toLocaleDateString()}</p>
                                <p><strong>To:</strong> {new Date(leave.toDate).toLocaleDateString()}</p>
                                <p><strong>Reason:</strong> {leave.reason}</p>
                                <p><strong>Status:</strong> {leave.status}</p>
                                <button onClick={() => handleCancelLeave(leave.id)}>Request Cancellation</button>
                                <button onClick={() => handleDeleteLeave(leave.id)}>Delete Leave</button>
                            </li>
                        ))}
                    </ul>

                    <button className="logout-button" onClick={handleLogout}>Logout</button>
                </>
            )}
        </div>
    );
};

export default StudentDashboard;
