import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate from react-router-dom

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('');
  const navigate = useNavigate(); // Initialize the navigate function

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!role) {
      alert('Please select a role.');
      return;
    }
    try {
      const response = await axios.post(`http://localhost:5000/api/${role}s/login`, { username, password });
      if (response.data.role === 'student') {
        navigate('/student-dashboard'); // Use navigate for routing
      } else {
        navigate('/admin-dashboard'); // Use navigate for routing
      }
    } catch (error) {
      console.error('Login failed:', error);
      alert('Login failed. Please check your credentials.'); // Consider improving this alert
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <select onChange={(e) => setRole(e.target.value)} required>
          <option value="">Select Role</option>
          <option value="student">Student</option>
          <option value="admin">Admin</option>
        </select>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
