import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [leaves, setLeaves] = useState([]);

  useEffect(() => {
    const fetchLeaves = async () => {
      const response = await axios.get('/api/admins/leaves'); // Admin route to fetch all leaves
      setLeaves(response.data);
    };
    fetchLeaves();
  }, []);

  const handleApprove = async (leaveId) => {
    await axios.put('/api/admins/leaves/status', { leaveId, status: 'approved' });
    alert('Leave approved!');
    // Refresh leaves after approval
    const response = await axios.get('/api/admins/leaves');
    setLeaves(response.data);
  };

  const handleDeny = async (leaveId) => {
    await axios.put('/api/admins/leaves/status', { leaveId, status: 'denied' });
    alert('Leave denied!');
    // Refresh leaves after denial
    const response = await axios.get('/api/admins/leaves');
    setLeaves(response.data);
  };

  return (
    <div>
      <h2>Admin Dashboard</h2>
      <h3>Pending Leave Requests</h3>
      <ul>
        {leaves.map((leave) => (
          <li key={leave.id}>
            {leave.type} from {leave.fromDate} to {leave.toDate} - Status: {leave.status}
            <button onClick={() => handleApprove(leave.id)}>Approve</button>
            <button onClick={() => handleDeny(leave.id)}>Deny</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDashboard;
