import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState('student');
    const [errorMessage, setErrorMessage] = useState(''); // State for error message
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        const apiUrl = role === 'student' ? '/students/login' : '/admins/login';
        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}${apiUrl}`, { username, password });
            localStorage.setItem('token', response.data.token);
            // Redirect based on role
            if (role === 'student') {
                navigate('/student-dashboard');
            } else {
                navigate('/admin-dashboard');
            }
        } catch (error) {
            setErrorMessage(error.response?.data?.message || 'Unexpected error occurred'); // Set error message
        }
    };

    return (
        <div className="login-container">
            <h2>Login</h2>
            <form onSubmit={handleLogin}>
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <select onChange={(e) => setRole(e.target.value)} value={role}>
                    <option value="student">Student</option>
                    <option value="admin">Admin</option>
                </select>
                <button type="submit">Login</button>
            </form>
            {errorMessage && <div id="error-container" style={{ color: 'red' }}>{errorMessage}</div>} {/* Error message display */}
        </div>
    );
};

export default Login;
