import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Navbar from '../components/Navbar';

function AdminDashboard() {
    const [leaveRequests, setLeaveRequests] = useState([]);

    useEffect(() => {
        const fetchLeaveRequests = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get('http://localhost:5000/api/leaves', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setLeaveRequests(response.data);
            } catch (error) {
                console.error('Error fetching leave requests:', error.response.data.message);
            }
        };
        fetchLeaveRequests();
    }, []);

    const handleLeaveAction = async (id, action) => {
        try {
            const token = localStorage.getItem('token');
            await axios.patch(`http://localhost:5000/api/leaves/${action}/${id}`, {}, {
                headers: { Authorization: `Bearer ${token}` },
            });
            alert(`Leave ${action}ed successfully`);
            // Update the leaveRequests state after action
            setLeaveRequests(prevRequests => prevRequests.filter(request => request._id !== id));
        } catch (error) {
            console.error(`Error ${action}ing leave:`, error.response.data.message);
        }
    };

    return (
        <div>
            <Navbar />
            <h1>Admin Dashboard</h1>
            <ul>
                {leaveRequests.map(request => (
                    <li key={request._id}>
                        {request.student.username} applied for {request.typeOfLeave} leave from {request.fromDate} to {request.toDate}
                        <button onClick={() => handleLeaveAction(request._id, 'approve')}>Approve</button>
                        <button onClick={() => handleLeaveAction(request._id, 'reject')}>Reject</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default AdminDashboard;
