import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate from react-router-dom

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState(''); // State to hold the selected role
    const navigate = useNavigate(); // Initialize the navigate function

    const handleLogin = async (e) => {
        e.preventDefault();

        // Validate inputs
        if (!role) {
            alert('Please select a role.');
            return;
        }
        if (!username || !password) {
            alert('Please enter both username and password.');
            return;
        }

        try {
            const response = await axios.post(`http://localhost:5000/api/${role}s/login`, { username, password });

            // Assuming the backend sends back { id: <studentId>, role: <role> }
            if (response.data.success) {
                if (role === 'student') {
                    localStorage.setItem('studentId', response.data.id); // Store studentId in local storage
                    alert('Login successful');
                    navigate('/student-dashboard'); // Navigate to student dashboard
                } else if (role === 'admin') {
                    alert('Login successful');
                    navigate('/admin-dashboard'); // Navigate to admin dashboard
                }
            } else {
                alert('Login failed. Please check your credentials.');
            }
        } catch (error) {
            console.error('Login failed:', error);
            const errorMessage = error.response?.data?.message || 'Login failed. Please check your credentials.';
            alert(errorMessage); // Show a more detailed alert message
        }
    };

    return (
        <div>
            <h2>Login</h2>
            <form onSubmit={handleLogin}>
                <select onChange={(e) => setRole(e.target.value)} required>
                    <option value="">Select Role</option>
                    <option value="student">Student</option>
                    <option value="admin">Admin</option>
                </select>
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit">Login</button>
            </form>
        </div>
    );
};

export default Login;
