import React, { useState } from 'react';
import axios from 'axios';

const ApplyLeave = () => {
    const [type, setType] = useState('');
    const [fromDate, setFromDate] = useState('');
    const [toDate, setToDate] = useState('');
    const [reason, setReason] = useState('');

    const handleApply = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');

        try {
            await axios.post('http://localhost:5000/api/leaves/apply', { type, fromDate, toDate, reason }, {
                headers: { Authorization: `Bearer ${token}` },
            });
            alert('Leave applied successfully');
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div>
            <h2>Apply for Leave</h2>
            <form onSubmit={handleApply}>
                <input type="text" placeholder="Type of Leave" value={type} onChange={(e) => setType(e.target.value)} required />
                <input type="date" value={fromDate} onChange={(e) => setFromDate(e.target.value)} required />
                <input type="date" value={toDate} onChange={(e) => setToDate(e.target.value)} required />
                <textarea placeholder="Reason" value={reason} onChange={(e) => setReason(e.target.value)} required />
                <button type="submit">Apply</button>
            </form>
        </div>
    );
};

export default ApplyLeave;
