import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AdminDashboard = () => {
    const [leaves, setLeaves] = useState([]);
    const [cancellationRequests, setCancellationRequests] = useState([]);
    const token = localStorage.getItem('token');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchLeaves = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/leaves/admin`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setLeaves(response.data.filter(leave => leave.status !== 'cancellation_pending'));
                setCancellationRequests(response.data.filter(leave => leave.status === 'cancellation_pending'));
            } catch (error) {
                console.error("Error fetching leaves:", error);
            }
        };

        fetchLeaves();
    }, [token]);

    const handleUpdateLeaveStatus = async (leaveId, status) => {
        try {
            await axios.post(`${process.env.REACT_APP_API_URL}/leaves/status`, { id: leaveId, status }, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setLeaves(leaves.map(leave => (leave.id === leaveId ? { ...leave, status } : leave)));
        } catch (error) {
            console.error("Error updating leave status:", error);
        }
    };

    const handleCancellationStatus = async (leaveId, status) => {
        try {
            await axios.post(`${process.env.REACT_APP_API_URL}/leaves/cancel/status`, { id: leaveId, status }, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setCancellationRequests(cancellationRequests.filter(leave => leave.id !== leaveId));
        } catch (error) {
            console.error("Error updating cancellation status:", error);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/');
    };

    return (
        <div className="admin-dashboard">
            <h2>Welcome, Admin!</h2>
            <h3>Leave Requests</h3>
            <ul>
                {leaves.map((leave) => (
                    <li key={leave.id}>
                        <p><strong>Username:</strong> {leave.username}</p>
                        <p><strong>Leave Type:</strong> {leave.type}</p>
                        <p><strong>From:</strong> {new Date(leave.fromDate).toLocaleDateString()}</p>
                        <p><strong>To:</strong> {new Date(leave.toDate).toLocaleDateString()}</p>
                        <p><strong>Reason:</strong> {leave.reason}</p>
                        <p><strong>Status:</strong> {leave.status}</p>
                        <button onClick={() => handleUpdateLeaveStatus(leave.id, 'approved')}>Approve</button>
                        <button onClick={() => handleUpdateLeaveStatus(leave.id, 'denied')}>Deny</button>
                    </li>
                ))}
            </ul>

            <h3>Cancellation Requests</h3>
            <ul>
                {cancellationRequests.map((leave) => (
                    <li key={leave.id}>
                        <p><strong>Username:</strong> {leave.username}</p>
                        <p><strong>Leave Type:</strong> {leave.type}</p>
                        <p><strong>From:</strong> {new Date(leave.fromDate).toLocaleDateString()}</p>
                        <p><strong>To:</strong> {new Date(leave.toDate).toLocaleDateString()}</p>
                        <p><strong>Reason:</strong> {leave.reason}</p>
                        <p><strong>Status:</strong> {leave.status}</p>
                        <button onClick={() => handleCancellationStatus(leave.id, 'approved')}>Approve Cancellation</button>
                        <button onClick={() => handleCancellationStatus(leave.id, 'denied')}>Deny Cancellation</button>
                    </li>
                ))}
            </ul>

            <button className="logout-button" onClick={handleLogout}>Logout</button>
        </div>
    );
};

export default AdminDashboard;
