import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

const AdminDashboard = () => {
    const [leaves, setLeaves] = useState([]);
    const [cancellationRequests, setCancellationRequests] = useState([]);
    const token = localStorage.getItem('token');
    const navigate = useNavigate(); // Initialize useNavigate

    useEffect(() => {
        const fetchLeavesAndRequests = async () => {
            try {
                const leavesResponse = await axios.get(`${process.env.REACT_APP_API_URL}/leaves/admin`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setLeaves(leavesResponse.data);

                const requestsResponse = await axios.get(`${process.env.REACT_APP_API_URL}/cancel-requests`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setCancellationRequests(requestsResponse.data);
            } catch (error) {
                console.error("Error fetching leaves:", error);
            }
        };

        fetchLeavesAndRequests();
    }, [token]);

    const handleUpdateLeaveStatus = async (leaveId, status) => {
        try {
            await axios.post(`${process.env.REACT_APP_API_URL}/leaves/status`, { id: leaveId, status }, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setLeaves(leaves.map(leave => (leave.id === leaveId ? { ...leave, status } : leave)));
        } catch (error) {
            console.error("Error updating leave status:", error);
        }
    };

    const handleDecision = async (requestId, decision) => {
        try {
            await axios.post(`${process.env.REACT_APP_API_URL}/cancel-requests/${requestId}/decide`, { decision }, {
                headers: { Authorization: `Bearer ${token}` }
            });
            alert('Decision processed successfully.');
            // Optionally refresh the requests list
            setCancellationRequests(cancellationRequests.filter(req => req.id !== requestId));
        } catch (error) {
            console.error("Error processing decision:", error);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token'); // Clear the token from local storage
        navigate('/'); // Redirect to the login page
    };

    return (
        <div className="admin-dashboard">
            <h2>Welcome, Admin!</h2>
            <h3>Leave Requests</h3>
            <ul>
                {leaves.map((leave) => (
                    <li key={leave.id}>
                        <p><strong>Username:</strong> {leave.username}</p>
                        <p><strong>Leave Type:</strong> {leave.type}</p>
                        <p><strong>From:</strong> {new Date(leave.fromDate).toLocaleDateString()}</p>
                        <p><strong>To:</strong> {new Date(leave.toDate).toLocaleDateString()}</p>
                        <p><strong>Reason:</strong> {leave.reason}</p>
                        <p><strong>Status:</strong> {leave.status}</p>
                        <button className="approve-button" onClick={() => handleUpdateLeaveStatus(leave.id, 'approved')}>Approve</button>
                        <button className="deny-button" onClick={() => handleUpdateLeaveStatus(leave.id, 'denied')}>Deny</button>
                    </li>
                ))}
            </ul>

            <h3>Cancellation Requests</h3>
            <ul>
                {leaves.map((leave) => (
                    <li key={leave.id}>
                       <p> <strong>Username:</strong> {leave.username} </p>
                       <p> <strong>LeaveType:</strong> {leave.type} </p>
                       <p><strong>From:</strong> {new Date(leave.fromDate).toLocaleDateString()}  </p>
                       <p><strong>To:</strong> {new Date(leave.toDate).toLocaleDateString()}</p>
                       <p><strong>Reason:</strong> {leave.reason}</p>
                       <p><strong>Status:</strong> {leave.status}</p>
                       <p><button className="approve-button" onClick={() => handleUpdateLeaveStatus(leave.id, 'approved')}>Approve</button></p>
                       <p><button className="deny-button" onClick={() => handleUpdateLeaveStatus(leave.id, 'denied')}>Cancel</button></p>
                    </li>
                ))}
            </ul>

            {/* Logout Button */}
            <button className="logout-button" onClick={handleLogout}>
                Logout
            </button>
        </div>
    );
};

export default AdminDashboard;
